var productName=document.getElementById("productName"),productCategory=document.getElementById("productCategory"),price=document.getElementById("price"),data=document.getElementById("tabledata"),productList=[];function addproduct(){var productItem={productName:productName.value,productCategory:productCategory.value,price:Number(price.value)};productList.push(productItem),displayProduct(),cleardata()}function displayProduct(){var wallet="";for(let index=0;index<productList.length;index++)wallet+=`<tr>\n        <th scope="row">${index+1}</th>\n        <td>${productList[index].productName}</td>\n        <td>${productList[index].productCategory}</td>\n        <td>${productList[index].price}</td>\n        <td><button class="btn btn-danger" type="button" onclick="deletedata(${index})">Remove From Cart</button>\n            <button class="btn btn-warning" type="button">Edit</button>\n        </td>\n\n      </tr>`;data.innerHTML=wallet}function cleardata(){productName.value=null,productCategory.value=null,price.value=null}function deletedata(index){productList.splice(index,1),displayProduct()}